#!/usr/bin/env node
'use strict';

function _interopDefault (ex) { return (ex && (typeof ex === 'object') && 'default' in ex) ? ex['default'] : ex; }

var http = _interopDefault(require('http'));

var itAlive = null;

var SSE = function SSE(req, res) {
    var aliveInterval = 60000;

    req.socket.setNoDelay(true);

    res.writeHead(200, {
        'Access-Control-Allow-Origin': '*',
        'Content-Type': 'text/event-stream',
        'Cache-Control': 'no-cache',
        'Connection': 'keep-alive'
    });

    itAlive = setInterval(function () {
        res.write(':ping\n\n');
    }, aliveInterval);

    return {
        send: function send(data) {
            res.write('data: ' + data + '\n\n');
        },
        close: function close(callback) {
            res.on('close', function () {
                if (itAlive) clearInterval(itAlive);
                if (callback) callback();
            });
        }
    };
};

function createServer(currency, port, cb) {
    const server = http.createServer((req, res) => {
      if (req.url !== `/rate/${currency}`) return res.end();
  
      const client = SSE(req, res);
      cb(client); // Assuming cb is defined elsewhere
    });
  
    server.listen(port, () => {
      console.log(`SSE FAKE SERVER for ${currency}`);
      console.log(`Server running, listening at port ${port}.`);
    });
  }
  

module.exports = createServer;
